import Logo from "../static/game-warrior/img/BoardHub-Logo-Tp.png";
import "../static/game-warrior/css/join.css";
import { useState, useRef } from "react";
import { UserOutlined } from '@ant-design/icons';
import { Avatar, Space } from 'antd';
import Swal from "sweetalert2";
function Entryinfo() {
  //이메일 이벤트
  const [inputChangeEmail, setInputChangeEmail] = useState("");
  const [Image, setImage] = useState("https://cdn.pixabay.com/photo/2015/10/05/22/37/blank-profile-picture-973460_1280.png");
  const fileInput = useRef(null);

  const onChangeEmail = (e) => {
    setInputChangeEmail(e.target.value);
  };

  //이메일 인증 버튼 클릭
  const mailConfirm = () =>{
      console.log("email =" + inputChangeEmail)
      fetch('http://localhost:8080/mailconfirm',{
        method: 'POST',
        headers: {'Content-Type':'application/json'},
        body: JSON.stringify(inputChangeEmail)
      })
      .then(response => response.json())
      .then(data => {
        console.log("인증번호 : " + data);
        Swal.fire({
          icon: "info",
          title: "인증 번호 발송",
          text: "해당 메일로 인증 번호가 발송되었습니다. 확인부탁드립니다.",
          showCancelButton: true,
          confirmButtonText: "확인"
        })
        .catch(error => {
          console.log("응답실패" + error);
        })
      })
  
  }

  // 프로필 사진 
  const onChange = (e) => {
    if (e.target.files[0]) {
      // 화면에 프로필 사진 표시
      const reader = new FileReader();
      reader.onload = () => {
        if (reader.readyState === 2) {
          setImage(reader.result);
        }
      };
      reader.readAsDataURL(e.target.files[0]);
    } else { // 업로드 취소할 시
      setImage("https://cdn.pixabay.com/photo/2015/10/05/22/37/blank-profile-picture-973460_1280.png");
    }
  };

  return (
    <div className="body">
      <section className="wrap">
        <div className="logo" style={{
          backgroundImage: `url(${Logo})`,
          width: '200px',
          height: '170px',
          backgroundSize: 'cover'
        }}></div>
        <div className="entry-wrap">
          <p className="label-box">
            <label>이메일</label>
          </p>
          <div className="email-box">
            <div className="email-input-box">
              <input type="email" name="user_email" onChange={onChangeEmail} />
            </div>
            <div className="send-email-box">
              <button className={`send-email-btn ${inputChangeEmail === "" ? 'disabled' : ''}`} disabled={inputChangeEmail === ""} onClick={mailConfirm}>인증메일 발송</button>
            </div>
          </div>
          <p className="label-box">
            <label>인증번호</label>
          </p>
          <div className="email-box">
            <div className="email-input-box">
              <input type="text" name="verification" />
            </div>
            <div className="send-email-box">
              <button >인증번호 확인</button>
            </div>
          </div>
          <p className="label-box">
            <label>프로필 사진</label>
          </p>
          <div className="profile-box">
            <label for="upload-profile">
              <Space wrap size={200}>
                <Avatar shape="square" size={200} src={Image} icon={<UserOutlined />} />
              </Space>
            </label>
            <input
              id="upload-profile"
              type='file'
              style={{ display: 'none' }}
              accept='image/jpg,impge/png,image/jpeg'
              name='profile_img'
              onChange={onChange}
              ref={fileInput} />
          </div>
          <p className="label-box">
            <label>아이디</label>
          </p>
          <div className="email-box">
            <div className="email-input-box">
              <input type="text" name="username" />
            </div>
            <div className="send-email-box">
              <button >아이디 중복 체크</button>
            </div>
          </div>
          <p className="label-box">
            <label>비밀번호</label>
          </p>
          <div className="email-box">
            <div className="same-input-box">
              <input type="password" name="password" />
            </div>
          </div>
          <p className="label-box">
            <label>비밀번호 재입력</label>
          </p>
          <div className="email-box">
            <div className="same-input-box">
              <input type="password"  />
            </div>
          </div>
          <p className="label-box">
            <label>이름</label>
          </p>
          <div className="email-box">
            <div className="same-input-box">
              <input type="text" name="name" />
            </div>
          </div>
          <p className="label-box">
            <label>닉네임</label>
          </p>
          <div className="email-box">
            <div className="same-input-box">
              <input type="text" name="nickname" />
            </div>
          </div>
          <div className="send-btn-box">
            <button >다음</button>
          </div>
        </div>
      </section>
    </div>
  );
}

export default Entryinfo;
