import Logo from "../static/game-warrior/img/BoardHub-Logo-Tp.png"
import "../static/game-warrior/css/join.css";
import { useState } from "react";
import { UserOutlined } from '@ant-design/icons';
import { Avatar, Space } from 'antd';

function Entryinfo() {
  const [inputChange, setInputChange] = useState("");

  const onChangebtn = (e) =>{
    setInputChange(e.target.value)
  }
  const [Image, setImage] = useState("https://cdn.pixabay.com/photo/2015/10/05/22/37/blank-profile-picture-973460_1280.png")
  const fileInput = useRef(null)

  const onChange = (e) => {
    if(e.target.files[0]){
              setFile(e.target.files[0])
          }else{ //업로드 취소할 시
              setImage("https://cdn.pixabay.com/photo/2015/10/05/22/37/blank-profile-picture-973460_1280.png")
              return
          }
    //화면에 프로필 사진 표시
          const reader = new FileReader();
          reader.onload = () => {
              if(reader.readyState === 2){
                  setImage(reader.result)
              }
          }
          reader.readAsDataURL(e.target.files[0])
      }
  

  return (
    <div className="body">
      <section className="wrap">
        <div className="logo" style={
          {
            backgroundImage:`url(${Logo})`,
            width: '200px',
            height: '170px',
            backgroundSize: 'cover'
          }
        }></div>
        <div className="entry-wrap">
          <p className="label-box">
            <label>이메일</label>
          </p>
          <div className="email-box">
            <div className="email-input-box">
              <input type="email" name="user_email" onChange={onChangebtn}/>
            </div>
            <div className="send-email-box">
              <button className={`send-email-btn ${inputChange === "" ? 'disabled' : ''}`} disabled={inputChange === ""}>인증메일 발송</button>
            </div>
          </div>
          <p className="label-box">
            <label>프로필 사진</label>
          </p>
          <div className="profile-box">
            <Space wrap size={200}>
              <Avatar shape="square" size={200} src={Image} icon={<UserOutlined />} />
            </Space>
            <input 
              type='file' 
              style={{display:'none'}}
              accept='image/jpg,impge/png,image/jpeg' 
              name='profile_img'
              onChange={onChange}
              ref={fileInput}/>
          </div>
        </div>
      </section>
    </div>
  );
}

export default Entryinfo;
